import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import config from "../config";
import { useSpring, animated } from "react-spring";
import { useSwipeable } from "react-swipeable";
import styles from "../Style/main.module.css";
import fetchHelper from "../utils/fetchHelper";
import ApiHandler from "./ApiHandlerPage";

export default function Main() {
  const navigate = useNavigate();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [bodyrecord, setbodyrecord] = useState([]);
  const [loading, setLoading] = useState(true);
  const [jwtString, setJwtString] = useState("");
  const useridRef = useRef(sessionStorage.getItem("userid"));

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => goToNext(),
    onSwipedRight: () => goToPrevious(),
  });

  const navigateMain = () => navigate("/main");
  const navigateToRecordBody = () => navigate("/recordbody");
  const navigateCalender = () => navigate("/Calender");
  const navigateGraph = () => navigate("/Graph");

  const handleLogout = async () => {
    try {
      const response = await fetchHelper(`http://${config.SERVER_URL}/login/logout`, {
        method: "POST",
        credentials: "include",
      });

      if (!response.ok) {
        if (response.status === 404) navigate("/error/404");
        else if (response.status === 500) navigate("/error/500");
        else if (response.status === 503) navigate("/error/503");
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      sessionStorage.removeItem("userid");
      navigate("/login");
    } catch (error) {
      console.error("로그아웃 중 오류 발생:", error);
    }
  };

  useEffect(() => {
    fetchHelper(`http://${config.SERVER_URL}/login/validate`, {
      method: "GET",
      credentials: "include",
    })
      .then((response) => {
        if (!response.ok) {
          if (response.status === 404) navigate("/error/404");
          else if (response.status === 500) navigate("/error/500");
          else if (response.status === 503) navigate("/error/503");
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        useridRef.current = data.userid;
        sessionStorage.setItem("userid", data.userid);
        return fetchHelper(`http://${config.SERVER_URL}/userinfobody/recentuserbody/${data.userid}`, {
          method: "GET",
          credentials: "include",
          headers: { "Content-Type": "application/json" },
        });
      })
      .then((response) => {
        if (!response.ok) {
          if (response.status === 404) navigate("/error/404");
          else if (response.status === 500) navigate("/error/500");
          else if (response.status === 503) navigate("/error/503");
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((bodyData) => {
        setbodyrecord(bodyData);
        setLoading(false);
      })
      .catch((error) => console.error("데이터 로딩 중 오류 발생:", error));
  }, [navigate]);

  return (
    <div className={styles.Main_Container}>
      <a className={styles.maintitle}>FitEnd</a>
      <img src="/image/black.png" alt="Background" className={styles.MainImage} />
      <div className={styles["Central-Menu"]}>
        <div className={styles.anime_container} {...swipeHandlers}>
          <animated.div style={animation} className={styles.slide}>
            <img src={images[currentIndex]} alt="carousel" />
          </animated.div>
          <div className={styles.anime_controls}>
            <button className={styles.prev} onClick={goToPrevious}>⟨</button>
            <button className={styles.next} onClick={goToNext}>⟩</button>
          </div>
        </div>
      </div>
    </div>
  );
}
