import { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import styles from "../Style/MealCalender.module.css"; // Import module CSS
import config from "../config";
import { FaCalendarAlt } from "react-icons/fa";

const MealCalendar = ({ mealData = [] }) => { // ‚úÖ mealData Í∏∞Î≥∏Í∞íÏùÑ Îπà Î∞∞Ïó¥Î°ú ÏÑ§Ï†ï
  const navigate = useNavigate();
  const [userid, setUserid] = useState("");
  const [year, setYear] = useState(2025);
  const [month, setMonth] = useState(3);
  const [selectedDate, setSelectedDate] = useState(null);
  const [mealRecords, setMealRecords] = useState({}); // ‚úÖ Ï†ïÎ¶¨Îêú Îç∞Ïù¥ÌÑ∞ Ï†ÄÏû•

  // üìå ÏõîÏùò ÏãúÏûë ÏöîÏùºÏùÑ ÏõîÏöîÏùº(0)Î∂ÄÌÑ∞ ÏãúÏûëÌïòÎèÑÎ°ù Ï°∞Ï†ï
  const rawStartDay = new Date(year, month - 1, 1).getDay();
  const startDay = (rawStartDay === 0 ? 6 : rawStartDay - 1); // 0(ÏùºÏöîÏùº) ‚Üí 6, Í∑∏ Ïô∏Îäî -1 Ï†ÅÏö©

  
  const navigateMain = () => {navigate("/main");};

  const navigateToRecordBody = () => {navigate("/recodbody");};

  const navigateFood=() => {navigate("/MealTimingselect");};

  const navigateGraph = () => {navigate("/Graph")};

  const handleLogout = async () => {
    await fetch(`http://${config.SERVER_URL}/login/logout`, {
        method: "POST",
        credentials: "include",
    });
    sessionStorage.removeItem("useridRef");
    navigate("/login");
  };

  // üìÖ ÎÇ†Ïßú Ìè¨Îß∑ Î≥ÄÌôò Ìï®Ïàò
  const formatDate = (date) => {
    return new Date(date.getTime() - date.getTimezoneOffset() * 60000)
      .toISOString()
      .split("T")[0];
  };

  useEffect(() => {
    if (!Array.isArray(mealRecords)) {
      // console.warn("mealDataÍ∞Ä Î∞∞Ïó¥Ïù¥ ÏïÑÎãôÎãàÎã§!", mealRecords);
      return;
    }
  
    const formattedData = mealRecords.reduce((acc, record) => {
      const dateKey = formatDate(new Date(record.timestamp));
  
      if (!acc[dateKey]) {
        acc[dateKey] = { enerc: 0, meal: [] };
      }
  
      acc[dateKey].enerc += record.enerc;
      acc[dateKey].meal.push(record.dietMemo);
      return acc;
    }, {});
    // ‚úÖ Í∏∞Ï°¥ Îç∞Ïù¥ÌÑ∞ÏôÄ Îã§Î•º Í≤ΩÏö∞ÏóêÎßå ÏóÖÎç∞Ïù¥Ìä∏
    if (JSON.stringify(mealRecords) !== JSON.stringify(formattedData)) {
      setMealRecords(formattedData);
    }
  }, [mealRecords, mealData]);

  useEffect(() => {
    // setSelectedDate(getTodayDate()); // Ïò§Îäò ÎÇ†ÏßúÎ°ú Í∏∞Î≥∏ ÏÑ§Ï†ï

    fetch(`http://${config.SERVER_URL}/login/validate`, {
      method: "GET",
      credentials: "include",
    })
      .then((response) => {
        if (!response.ok) throw new Error("Unauthorized");
        return response.json();
      })
      .then((data) => {
        console.log("Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ ÏÑ±Í≥µ:", data);
        setUserid(data.userid);

        return fetch(`http://${config.SERVER_URL}/food/diet-records/${data.userid}`, {
          method: "GET",
          credentials: "include",
          headers: { "Content-Type": "application/json" },
        });
      })
      .then((response) => {
        if (!response.ok) throw new Error("ÏÑúÎ≤Ñ ÏùëÎãµ Ïã§Ìå®");
        return response.json();
      })
      .then((data) => {
        console.log("Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞:", data);
        setMealRecords(data);
        // console.log("MealRecords:", mealRecords);
      })
      .catch((error) => {
        console.warn("Ïù∏Ï¶ù Ïã§Ìå® ÎòêÎäî Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
        navigate("/login");
      });
  }, [navigate]);


  // const isSelected = selectedDate === day;

  const handleDateClick = (day) => {
    setSelectedDate(day); // ‚úÖ ÎÇ†Ïßú Í∞ùÏ≤¥Í∞Ä ÏïÑÎãàÎùº Ïà´ÏûêÎ°ú Ï†ÄÏû•
  };  
  // ‚úÖ ÌòÑÏû¨ ÏõîÏùò Ï¥ù Ïùº Ïàò Í∞ÄÏ†∏Ïò§Í∏∞
  const daysInMonth = new Date(year, month, 0).getDate();

  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];
  
  const changeMonth = (offset) => {
    let newMonth = month + offset;
    let newYear = year;
  
    if (newMonth < 1) {
      newMonth = 12;
      newYear--;
    } else if (newMonth > 12) {
      newMonth = 1;
      newYear++;
    }
  
    // ‚úÖ Î≥ÄÍ≤ΩÎêòÏßÄ ÏïäÏùÄ Í≤ΩÏö∞ `setState` Ïã§Ìñâ Î∞©ÏßÄ
    if (newMonth !== month || newYear !== year) {
      setYear(newYear);
      setMonth(newMonth);
      setSelectedDate(null);
    }
  };
  

  return (
    <div className={styles.MealCalender_Container}>
      <img src="/image/black.png" alt="Background" className={styles.MealCalender_image} />
      <a className={styles.MealCalender_title}>FitEnd</a>
      <div className={styles.meal_calendar}>
       {/* üìå Ïõî Î≥ÄÍ≤Ω Ìó§Îçî */}
      <div className={styles.calendar_header}>
        <button onClick={() => changeMonth(-1)}>‚ü™</button>
        <h2>{monthNames[month - 1]} {year}</h2> {/* ‚úÖ Ïà´Ïûê ‚Üí ÏòÅÏñ¥Î°ú Î≥ÄÍ≤Ω */}
        <button onClick={() => changeMonth(1)}>‚ü´</button>
      </div>


        {/* ÏöîÏùº Ìó§Îçî */}
        <div className={styles.weekdays}>
          {["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"].map((day) => (
            <div key={day}>{day}</div>
          ))}
        </div>

        {/* ÎÇ†Ïßú ÌëúÏãú */}
        <div className={styles.calendar_days}>
          {Array.from({ length: daysInMonth + startDay }).map((_, index) => {
            const day = index - startDay + 1;
            const dateKey = `${year}-${month.toString().padStart(2, "0")}-${day.toString().padStart(2, "0")}`;
            const isSelected = selectedDate === day;
            const hasData = mealRecords[dateKey];
            
            return index < startDay ? (
              <div key={`empty-${index}`} className={styles.empty_day}></div> // Îπà Ïπ∏ Ï∂îÍ∞Ä
            ) : (
               <button
                key={day}
                onClick={() => handleDateClick(day)}
                className={`${styles.calendar_day} ${isSelected ? styles.selected : ""} ${hasData ? styles.hasData : ""}`}
              >
                {day}
              </button>
            );
          })}
        </div>

        {selectedDate && (
            <div className={styles.selected_date_container}>
              <p className={styles.selected_date_text}>{year}ÎÖÑ {month}Ïõî {selectedDate}Ïùº ÏÑ†ÌÉùÎê®</p>
              {/* üî• Debugging logs */}
              {/* {console.log("Î∞ÄÎ†àÏΩîÎìú:", mealRecords)}
              {console.log("Ï≤¥ÌÇπ Îç∞Ïù¥Ìä∏ ÌÇ§:", `${year}-${month.toString().padStart(2, "0")}-${selectedDate.toString().padStart(2, "0")}`)} */}

              {/* üî• Ìï¥Îãπ ÎÇ†ÏßúÏóê Îç∞Ïù¥ÌÑ∞Í∞Ä Ï°¥Ïû¨ÌïòÎäîÏßÄ ÌôïÏù∏ */}
              {mealRecords[`${year}-${month.toString().padStart(2, "0")}-${selectedDate.toString().padStart(2, "0")}`] ? (
                <div className={styles.meal_data_container}>
                  <p className={styles.meal_data_text}>
                    {mealRecords[`${year}-${month.toString().padStart(2, "0")}-${selectedDate.toString().padStart(2, "0")}`].meal.join(", ")}
                  </p>
                  <p className={styles.meal_data_text}>
