import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import config from "../config";

const MealCalender = () => {
  const [meals, setMeals] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchMeals() {
      try {
        const response = await fetch(`http://${config.SERVER_URL}/api/meals`);
        if (!response.ok) {
          if (response.status === 404) navigate("/error/404");
          else if (response.status === 500) navigate("/error/500");
          else if (response.status === 503) navigate("/error/503");
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const result = await response.json();
        setMeals(result);
      } catch (error) {
        setError("식사 일정 데이터를 불러오는 중 오류 발생.");
        console.error("식사 일정 데이터 요청 오류:", error);
      }
    }
    fetchMeals();
  }, [navigate]);

  return (
    <div>
      <h2>Meal Calendar</h2>
      {error ? <p style={{ color: "red" }}>{error}</p> : <p>식사 일정을 불러오는 중...</p>}
      {meals && <pre>{JSON.stringify(meals, null, 2)}</pre>}
    </div>
  );
};

export default MealCalender;
