import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import config from "../config";
import styles from "../Style/register.module.css";
import fetchHelper from "../utils/fetchHelper";

export default function Register() {
  const navigate = useNavigate();
  const [userInfo, setUserInfo] = useState({
    userid: "",
    password: "",
  });
  const [errorMessage, setErrorMessage] = useState(null);

  const handleRegister = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch(`http://${config.SERVER_URL}/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(userInfo),
      });

      if (!response.ok) {
        if (response.status === 404) navigate("/error/404");
        else if (response.status === 500) navigate("/error/500");
        else if (response.status === 503) navigate("/error/503");
        setErrorMessage("회원가입 실패! 상태 코드: " + response.status);
        return;
      }

      alert("회원가입 성공!");
      navigate("/login");
    } catch (error) {
      setErrorMessage("네트워크 오류 발생! 다시 시도해주세요.");
      console.error("회원가입 중 오류 발생:", error);
    }
  };

  return (
    <div>
      <h2>Register</h2>
      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
      <form onSubmit={handleRegister}>
        <input
          type="text"
          placeholder="ID"
          value={userInfo.userid}
          onChange={(e) => setUserInfo({ ...userInfo, userid: e.target.value })}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={userInfo.password}
          onChange={(e) => setUserInfo({ ...userInfo, password: e.target.value })}
          required
        />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}
